// Emojiawesome extension, https://github.com/datenstrom/yellow-extensions/tree/master/source/emojiawesome

class YellowEmojiawesome {
    const VERSION = "0.8.12";
    public $yellow;         // access to API
    
    // Handle initialisation
    public function onLoad($yellow) {
        $this->yellow = $yellow;
        $this->yellow->system->setDefault("emojiawesomeToolbarButtons", ":grinning: :smile: :angry: :frowning: :heart_eyes: :kissing_heart: :stuck_out_tongue_winking_eye: :joy: :heart: :fire: :star: :cookie: :coffee: :hand: :+1: :-1:");
    }
    
    // Handle page content of shortcut
    public function onParseContentShortcut($page, $name, $text, $type) {
        $output = null;
        if (($name=="ea" && $type=="inline") || $type=="symbol") {
            list($shortname, $style) = $this->yellow->toolbox->getTextArguments($text);
            if (preg_match("/^ea-(.+)/", $shortname, $matches)) $shortname = str_replace("-", "_", $matches[1]);
            if ($this->isShortname($shortname)) {
                $class = $this->normaliseClass(trim("ea ea-$shortname $style"));
                $output = "<i class=\"".htmlspecialchars($class)."\"";
                $output .= " aria-label=\"".htmlspecialchars("$shortname")."\"";
                $output .= "></i>";
            }
        }
        return $output;
    }
    
    // Handle content file editing
    public function onEditContentFile($page, $action, $email) {
        $page->rawData = $this->normaliseText($page->rawData, true, false);
    }

    // Handle page extra data
    public function onParsePageExtra($page, $name) {
        $output = null;
        if ($name=="header") {
            $extensionLocation = $this->yellow->system->get("coreServerBase").$this->yellow->system->get("coreExtensionLocation");
            $output = "<link rel=\"stylesheet\" type=\"text/css\" media=\"all\" href=\"{$extensionLocation}emojiawesome.css\" />\n";
        }
        return $output;
    }
    
    // Normalise emoji CSS class
    public function normaliseClass($text) {
        return str_replace(array("+1", "-1", "_"), array("plus1", "minus1", "-"), $text);
    }
    
    // Normalise text with emoji, convert UTF8 and shortcode
    public function normaliseText($text, $convertUtf8 = true, $convertShortcode = true) {
        if ($convertUtf8) {
            $lookup = array();
            foreach ($this->getLookupData() as $entry) {
                $lookup[$entry["utf8"]] = ":".$entry["shortname"].":";
            }
            krsort($lookup);
            $text = str_replace(array_keys($lookup), array_values($lookup), $text);
        }
        if ($convertShortcode) {
            $thisCompatible = $this;
            $callback = function ($matches) use ($thisCompatible) {
                $output = $thisCompatible->onParseContentShortcut(null, "", $matches[1], "symbol");
                if (is_null($output)) $output = $matches[0];
                return $output;
            };
            $text = preg_replace_callback("/\:([\w\+\-\_]+)\:/", $callback, $text);
        }
        return $text;
    }
    
    // Check if emoji shortname exists
    public function isShortname($shortname) {
        $found = false;
        foreach ($this->getLookupData() as $entry) {
            if ($entry["shortname"]==$shortname) {
                $found = true;
                break;
            }
        }
        return $found;
    }
    
